    function wrapLines(text, maxChars){
      var lines=[], L=(text||'').split(/\r?\n/);
      for (var i=0;i<L.length;i++){
        var par = (L[i]||'').replace(/\s+$/,''); 
        if (!par){ lines.push(''); continue; }
        var words = par.split(/\s+/);
        var cur='';
        for (var j=0;j<words.length;j++){
          var w = words[j];
          if ((cur + ' ' + w).trim().length > maxChars){
            if (cur) lines.push(cur.trim());
            cur = w;
          } else {
            cur = (cur ? cur + ' ' : '') + w;
          }
        }
        if (cur) lines.push(cur.trim());
      }
      return lines;
    }
    function drawRichText(g, text, x, y, maxWidthPx){
      var approxCharW = 6.2, lineH = 15;
      var maxChars = Math.max(10, Math.floor(maxWidthPx / approxCharW));
      var raw = (text||'').split(/\r?\n/);
      var lines = [];
      for (var i=0;i<raw.length;i++){
        var ln = raw[i] || '';
        var cls='card2-text';
        if (ln.startsWith('## ')){ cls='card2-title'; ln = ln.slice(3); }
        else if (ln.startsWith('# ')){ cls='card2-title'; ln = ln.slice(2); }
        var wrapped = wrapLines(ln, maxChars);
        if (wrapped.length===0) wrapped=[''];
        for (var k=0;k<wrapped.length;k++){
          lines.push({text: wrapped[k], cls: cls});
        }
      }
      for (var j=0;j<lines.length;j++){
        var tx = document.createElementNS(svg.namespaceURI,'text');
        tx.setAttribute('x', x);
        tx.setAttribute('y', (y + j*lineH));
        tx.setAttribute('class', lines[j].cls);
        tx.textContent = lines[j].text;
        g.appendChild(tx);
      }
      return {lineH: lineH, lines: lines};
    }

