    // Beobachtet Einblendungen/Ãnderungen im portraitCard-Bereich
    function normalizeTextBlock(txt){
      if(!txt) return;
      // Entferne fÃ¼hrende leere tspans und normalisiere dy
      var spans = Array.from(txt.querySelectorAll('tspan'));
      // Entferne nur am Anfang leere
      while (spans.length && (!spans[0].textContent || spans[0].textContent.trim()==='')) {
        spans[0].remove();
        spans.shift();
      }
      var first = true;
      for (var i=0;i<spans.length;i++){
        var t = spans[i];
        t.removeAttribute('y');
        t.removeAttribute('x');
        t.setAttribute('dy', first ? '0em' : '1.1em');
        first = false;
      }
    }

    function scanPortrait(){
      var pc = document.getElementById('portraitCard');
      if(!pc) return;
      // typische Struktur: ein <text> in der weiÃen Karte
      var txt = pc.querySelector('text');
      if (txt) normalizeTextBlock(txt);
    }

    // Initial und bei zukÃ¼nftigen Ãnderungen
    var root = document.documentElement;
    var mo = new MutationObserver(function(){
      scanPortrait();
    });
    mo.observe(root, {subtree:true, childList:true, attributes:true});
    // Erstscan
    scanPortrait();
  } catch(e){ /* still */ }
