  function bboxAbs(el){
    try{
      var b = el.getBBox(), m = el.getCTM(); if(!b||!m) return null;
      function T(x,y){ return { x: m.a*x + m.c*y + m.e, y: m.b*x + m.d*y + m.f }; }
      var p1=T(b.x,b.y), p2=T(b.x+b.width,b.y), p3=T(b.x,b.y+b.height), p4=T(b.x+b.width,b.y+b.height);
      var minX=Math.min(p1.x,p2.x,p3.x,p4.x), maxX=Math.max(p1.x,p2.x,p3.x,p4.x);
      var minY=Math.min(p1.y,p2.y,p3.y,p4.y), maxY=Math.max(p1.y,p2.y,p3.y,p4.y);
      return { x:minX, y:minY, width:(maxX-minX), height:(maxY-minY) };
    }catch(e){ return null; }
  }
  function clamp(pos,w,h){
    var sz = svgSize();
    var x=pos.x, y=pos.y;
    if (x + w > sz.w - 4) x = Math.max(4, sz.w - w - 4);
    if (y + h > sz.h - 4) y = Math.max(4, sz.h - h - 4);
    if (x < 4) x = 4; if (y < 4) y = 4;
    return {x:x,y:y};
  }
  function anchoredPos(anchorEl, w, h){
    var bb = bboxAbs(anchorEl); if(!bb) return {x:20,y:20};
    var rightX = bb.x + bb.width + 12;
    var leftX  = bb.x - 12 - w;
    var sz = svgSize();
    var preferRight = rightX + w <= sz.w - 4;
    var x = preferRight ? rightX : Math.max(4, leftX);
    return clamp({x:x, y:bb.y}, w, h);
  }

