  // Notes: pro Zeile ein <text> mit Inline-Styles
  function wrapLines(text, maxChars){
    var lines = [];
    (text || '').split(/\r?\n/).forEach(function(par){
      par = (par||'').trim();
      if (!par){ lines.push(''); return; }
      var words = par.split(/\s+/);
      var cur = '';
      for (var i=0;i<words.length;i++){
        var w = words[i];
        if ((cur + ' ' + w).trim().length > maxChars){
          if (cur) lines.push(cur.trim());
          cur = w;
        } else {
          cur = (cur ? cur + ' ' : '') + w;
        }
      }
      if (cur) lines.push(cur.trim());
    });
    return lines;
  }
  function drawLinesAsText(g, text, x, y, maxWidthPx){
    var approxCharW = 6.5, lineH = 14;
    var maxChars = Math.max(8, Math.floor(maxWidthPx / approxCharW));
    var lines = wrapLines(text, maxChars);
    for (var j=0;j<lines.length;j++){
      var tx = document.createElementNS(svg.namespaceURI,'text');
      tx.setAttribute('x', x);
      tx.setAttribute('y', (y + j*lineH));
      tx.setAttribute('xml:space','preserve');
      tx.setAttribute('fill', '#ffffff');
      tx.setAttribute('fill-opacity', '1');
      tx.setAttribute('stroke', 'none');
      tx.setAttribute('font-size', '12');
      tx.setAttribute('font-family', 'Arial, DejaVu Sans, sans-serif');
      tx.textContent = lines[j];
      g.appendChild(tx);
    }
    return {lineH: lineH, lines: lines};
  }
  function closeNotes(){
    var nc = document.getElementById('notesCard'); if (nc) nc.remove();
    var bd = document.getElementById('notesBackdrop'); if (bd) bd.remove();
