  function openNotes(id, anchorEl){
    closeNotes();
    var text = (NOTES[id] || '').trim();
    if (!text) text = 'Keine Notizen';

    var w = POPW, pad = 10, h0 = 240;
    var pos = anchoredPos(anchorEl, w, h0);

    var sz = svgSize();
    var bd = document.createElementNS(svg.namespaceURI,'rect');
    bd.setAttribute('id','notesBackdrop');
    bd.setAttribute('x', 0); bd.setAttribute('y', 0);
    bd.setAttribute('width', sz.w); bd.setAttribute('height', sz.h);
    bd.setAttribute('fill', 'transparent');
    notesLayer.appendChild(bd);
    bd.addEventListener('click', function(){ closeNotes(); }, false);

    var g = document.createElementNS(svg.namespaceURI,'g'); g.setAttribute('id','notesCard'); notesLayer.appendChild(g);
    var card = document.createElementNS(svg.namespaceURI,'rect');
    card.setAttribute('class','panel-bg'); card.setAttribute('x', pos.x); card.setAttribute('y', pos.y);
    card.setAttribute('width', w); card.setAttribute('height', h0); g.appendChild(card);

    var innerX = pos.x + pad, innerY = pos.y + pad + 12;
    var info = drawLinesAsText(g, text, innerX, innerY, (w - 2*pad));
    var totalH = info.lines.length * info.lineH + 14;
    var finalH = Math.min(Math.max(80, totalH + 2*pad), 280);

    var cl = clamp(pos, w, finalH);
    var dx = cl.x - pos.x, dy = cl.y - pos.y;
    if (dx || dy){ g.setAttribute('transform', 'translate(' + dx + ',' + dy + ')'); }
    card.setAttribute('x', cl.x); card.setAttribute('y', cl.y); card.setAttribute('height', finalH);

    g.addEventListener('click', function(ev){ closeNotes(); ev.stopPropagation(); }, false);
    svg.appendChild(notesLayer); // Layer oben halten
  }

